function ic_apply_range_slider(a){if(typeof a.ionRangeSlider!=="function"){return}var i=1;var e=a.data("unit");var r=a.data("min");var n=a.data("max");var t=a.data("current-min");var o=a.data("current-max");if(n-r<2){i=.1}if(t===o){if(t===r){o=o+i}else{t=t-i}}a.ionRangeSlider({type:"double",min:r,max:n,from:t,to:o,step:i,postfix:" "+e,grid:true,hide_min_max:true,onFinish:function(){var i=a.closest("form");i.submit();a.closest(".product_size_filter").addClass("active")}})}jQuery(document).ready(function(a){a("body").on("reload",".ic-slider-container",function(){var i=a(this);i.removeClass("toReload");i.find(".ic-range-slider").each(function(){var i=a(this);ic_apply_range_slider(i)})});a(".ic-slider-container.toReload").trigger("reload")});